module.exports = { deleteTestRun };

const getBackendStore = require('../utils/get-backend-store');
const createStore = require('./store');

async function deleteTestRun(testId) {
  const store = createStore(await getBackendStore());
  await store.init();
  const result = await store.deleteTestRun(testId);
  return result;
}
