module.exports = { listTestRuns };

const createStore = require('./store');
const getBackendStore = require('../utils/get-backend-store');

async function listTestRuns(tags = [], limit, offset, status, created) {
  const store = createStore(await getBackendStore());
  await store.init();
  const results = await store.listTestRuns(tags, limit, offset, status, created);
  return results;
}